import Axios from 'axios'
import { useState } from 'react'
function App() {

  const [bookList, setBookList] = useState([]);

  const getBooks = () => {
    Axios.get('http://localhost:3001/books').then((response) => {
      setBookList(response.data);
    });
  }
  return (
    <div className="App container">
      <h1>Books</h1>
      <div className="book">
        <form action="">
          <div className="mb-3">
            <label htmlFor="name" className="form-label">Name</label>
            <input type="text" className="form-control" placeholder="Enter Name"></input>
          </div>
          <div className="mb-3">
            <label htmlFor="name" className="form-label">Author</label>
            <input type="text" className="form-control" placeholder="Enter Author"></input>
          </div>
          <buttob className="btn btn-success">Add Book</buttob>
        </form>
      </div>

      <hr/>

      <div className="books">
        <button className="btn btn-primary" onClick={getBooks}>Show Books</button>

        { bookList.map((val, key) => {
            return (
              <div className="book card">
                <div className="card-body text-left">
                  <p className="card-text">Name: {val.name}</p>
                  <p className="card-text">Author: {val.author}</p>
                </div>
              </div>
            )
          })}
      </div>

    </div>
  );
}

export default App;
